function generateWelcomeMessage(userData) {
  // æ£€æŸ¥ userData æ˜¯å¦å­˜åœ¨ï¼Œå¦‚æœä¸º null æˆ– undefinedï¼Œåˆ™æä¾›é»˜è®¤ä¿¡æ¯
  if (userData === null || userData === undefined) {
