// åœºæ™¯ï¼šåˆ›å»ºä¸€ä¸ªRPGæ¸¸æˆè§’è‰²ç”Ÿæˆå™¨
function createRPGCharacter(name) {
  // --- è§’è‰²çš„ç§æœ‰å±æ€§ (å°è£…åœ¨å‡½æ•°ä½œç”¨åŸŸå†…) ---
  let level = 1;
  let health = 100;
  let experience = 0;
  const EXP_TO_LEVEL_UP = 100;

  console.log(`ğŸŒŸ å†’é™©å®¶ "${name}" è¯ç”Ÿäº†ï¼`);

  // --- å†…éƒ¨ç§æœ‰å‡½æ•°ï¼Œå¤–éƒ¨æ— æ³•è°ƒç”¨ ---
  function gainLevel() {
    level++;
    health += 50; // å‡çº§å›è¡€å¹¶å¢åŠ ç”Ÿå‘½ä¸Šé™
    experience = 0; // ç»éªŒå€¼é‡ç½®
    console.log(`ğŸ‰ å‡çº§ï¼ ${name} ç°åœ¨æ˜¯ ${level} çº§ï¼ç”Ÿå‘½å€¼ +50ï¼`);
  }

  // --- è¿”å›ä¸€ä¸ªå…¬å…±æ¥å£å¯¹è±¡ï¼Œå®ƒèƒ½è®¿é—®å’Œæ“ä½œä¸Šé¢çš„ç§æœ‰å±æ€§ ---
  return {
    // æ–¹æ³•ï¼šå±•ç¤ºçŠ¶æ€
    showStatus: function() {
      console.log(
        `--- è§’è‰²çŠ¶æ€ ---\n` +
        `  åå­—: ${name}\n` +
        `  ç­‰çº§: ${level}\n` +
        `  ç”Ÿå‘½: ${health}\n` +
        `  ç»éªŒ: ${experience}/${EXP_TO_LEVEL_UP}\n` +
        `------------------`
      );
    },
    // æ–¹æ³•ï¼šæˆ˜æ–—è·å¾—ç»éªŒ
    gainExperience: function(amount) {
      console.log(`ğŸ’ª ${name} è·å¾—äº† ${amount} ç‚¹ç»éªŒï¼`);
      experience += amount;
      if (experience >= EXP_TO_LEVEL_UP) {
        gainLevel(); // è°ƒç”¨å†…éƒ¨çš„ç§æœ‰å‡½æ•°
      }
    },
    // æ–¹æ³•ï¼šå—åˆ°ä¼¤å®³
    takeDamage: function(damage) {
        health -= damage;
        console.log(`ğŸ’¥ ${name} å—åˆ°äº† ${damage} ç‚¹ä¼¤å®³ï¼Œå‰©ä½™ç”Ÿå‘½ ${health}ã€‚`);
        if (health <= 0) {
            console.log(`ğŸ’€ ${name} è¢«å‡»è´¥äº†... æ¸¸æˆç»“æŸï¼`);
        }
    }
  };
}

