  // é€’å½’æ­¥éª¤ï¼š
  // å–å‡ºç¬¬ä¸€ä¸ªå…ƒç´ 
  const firstElement = arr[0];
  // å¯¹å‰©ä½™çš„æ•°ç»„éƒ¨åˆ†è°ƒç”¨è‡ªèº«
  const restOfArray = arr.slice(1);

  console.log(`é€’å½’æ­¥éª¤: å–å‡º ${firstElement}, é€’å½’è®¡ç®— [${restOfArray}] çš„å’Œ`);

  return firstElement + sumArray(restOfArray);
}

