// (...fns) => (initialValue) => ... è¿™æ˜¯ä¸€ä¸ªé«˜é˜¶å‡½æ•°çš„å†™æ³•
const compose = (...fns) => (initialValue) => fns.reduceRight((acc, fn) => fn(acc), initialValue);

