function translateToKlingon(word, isDeviceOnline) {
  console.log(`\n--- å°è¯•ç¿»è¯‘å•è¯: "${word}" | è®¾å¤‡çŠ¶æ€: ${isDeviceOnline ? 'åœ¨çº¿' : 'ç¦»çº¿'} ---`);

