function calculateCircleArea(radius) {
  console.log(`å°è¯•è®¡ç®—åŠå¾„ä¸º ${radius} çš„åœ†é¢ç§¯...`);

  if (typeof radius !== 'number') {
    // å¦‚æœè¾“å…¥ä¸æ˜¯æ•°å­—ï¼Œå°±ä¸»åŠ¨æŠ›å‡ºä¸€ä¸ªç±»å‹é”™è¯¯
    throw new TypeError("å‚æ•°å¿…é¡»æ˜¯æ•°å­—ï¼");
