    throw new Error("åŠå¾„ä¸èƒ½ä¸ºè´Ÿæ•°ï¼");
  }

  const area = Math.PI * radius * radius;
  console.log(`è®¡ç®—æˆåŠŸï¼Œé¢ç§¯æ˜¯: ${area.toFixed(2)}`);
  return area;
}

