// å® ç‰©åˆæˆå™¨å‡½æ•°
function createHybridPet(petA, petB) {
  console.log(`ğŸŒ€ å¼€å§‹åˆæˆ... ${petA.name} å’Œ ${petB.name} è¿›å…¥äº†èåˆèˆ±ï¼`);

  // åå­—èåˆï¼šå–ç¬¬ä¸€ä¸ªå® ç‰©åå­—çš„å‰åŠéƒ¨åˆ†å’Œç¬¬äºŒä¸ªå® ç‰©åå­—çš„ååŠéƒ¨åˆ†
  const nameA_part = petA.name.slice(0, Math.ceil(petA.name.length / 2));
  const nameB_part = petB.name.slice(Math.floor(petB.name.length / 2));
  const hybridName = nameA_part + nameB_part;

  // ä½¿ç”¨å±•å¼€è¿ç®—ç¬¦åˆå¹¶æŠ€èƒ½ï¼Œå¹¶ç”¨ Set å»é™¤é‡å¤æŠ€èƒ½
  const combinedSkills = [...new Set([...petA.skills, ...petB.skills])];

  // ä½¿ç”¨å±•å¼€è¿ç®—ç¬¦æ„å»ºæ–°å® ç‰©å¯¹è±¡
  // åŸºç¡€å±æ€§æ¥è‡ªå® ç‰©Aï¼Œä½†åå­—å’ŒæŠ€èƒ½æ˜¯æ–°åˆæˆçš„
  // åŒæ—¶ï¼Œæˆ‘ä»¬ç»™æ–°å® ç‰©ä¸€ä¸ªç‹¬ç‰¹çš„â€œæ··åˆâ€ç±»å‹
  const hybridPet = {
    ...petA, // ç»§æ‰¿å® ç‰©Açš„åŸºç¡€å±æ€§ï¼ˆå¦‚emoji, hpï¼‰
    name: hybridName,
    type: 'æ··åˆå‹',
    skills: combinedSkills,
    parents: [petA.name, petB.name] // è®°å½•çˆ¶æ¯
  };

  console.log(`âœ¨ åˆæˆæˆåŠŸï¼è¯ç”Ÿäº†æ–°çš„å® ç‰©: ${hybridPet.emoji} ${hybridPet.name}!`);
  return hybridPet;
}

