function calculateJumpFuel(targetPlanet, currentFuel) {
  console.log(`ğŸ›°ï¸ æ­£åœ¨è®¡ç®—å‰å¾€ '${targetPlanet}' çš„èˆªçº¿...`);
  const planetDistances = {
    'ç«æ˜Ÿ': 50,
    'æœ¨æ˜Ÿ': 200,
    'åŠäººé©¬åº§Î±æ˜Ÿ': 1000,
    'æœªçŸ¥é¢†åŸŸ': Infinity
  };

  const requiredFuel = planetDistances[targetPlanet];

  if (requiredFuel === undefined) {
    throw new NavigationError(`é”™è¯¯ï¼šç›®æ ‡æ˜Ÿçƒ '${targetPlanet}' ä¸åœ¨æ˜Ÿå›¾ä¸­ï¼`);
  }

  if (requiredFuel > currentFuel) {
    throw new InsufficientFuelError(requiredFuel, currentFuel);
  }

  return `âœ… è®¡ç®—å®Œæˆï¼å‰å¾€ ${targetPlanet} éœ€è¦ ${requiredFuel} å•ä½ç‡ƒæ–™ã€‚å‡†å¤‡è·³è·ƒï¼`;
}

