    } else if (error instanceof InsufficientFuelError) {
      console.error(`â›½ï¸ ç‡ƒæ–™è­¦å‘Š: ${error.message}`);
      console.error(`   æˆ‘ä»¬è¿˜å·® ${error.requiredFuel - error.availableFuel} å•ä½ç‡ƒæ–™æ‰èƒ½åˆ°è¾¾ï¼`);
    } else {
      console.error(`ğŸš¨ æœªçŸ¥ç³»ç»Ÿæ•…éšœ: ${error.message}`);
    }
  }
}

