function scanAsteroid(name) {
  console.log(`ğŸ›°ï¸ å‘é€æ¢æµ‹å™¨å‰å¾€å°è¡Œæ˜Ÿ ${name}...`);
  return new Promise((resolve, reject) => {
    const scanTime = Math.random() * 2000 + 500; // éšæœºæ‰«ææ—¶é—´
    setTimeout(() => {
      const successChance = Math.random();
      if (successChance > 0.4) {
        const minerals = ['é’›', 'é’»çŸ³', 'è¶…å¯¼çŸ¿çŸ³'][Math.floor(Math.random() * 3)];
        resolve(`åœ¨ ${name} å‘ç°å¤§é‡ ${minerals}!`);
      } else if (successChance > 0.2) {
        reject(new Error(`æ¥è‡ª ${name} çš„èƒ½é‡æŠ¤ç›¾å¹²æ‰°ï¼Œæ‰«æå¤±è´¥ï¼`));
      } else {
        reject(new Error(`å°è¡Œæ˜Ÿå¸¦è¿‡äºå¯†é›†ï¼Œæ— æ³•æ¥è¿‘ ${name}ï¼`));
      }
    }, scanTime);
  });
}

// ä¸»æ§ç¨‹åºï¼šå¯åŠ¨æ˜Ÿé™…æ¢ç´¢ä»»åŠ¡
async function startExploration() {
  console.log("ğŸš€ èˆ°é•¿ï¼Œæ˜Ÿé™…çŸ¿ç‰©æ¢æµ‹ä»»åŠ¡å¯åŠ¨ï¼");
  const asteroids = ['C-53', 'X-7', 'Z-99'];
  const scanPromises = asteroids.map(name => scanAsteroid(name));

  // æˆ‘ä»¬ä½¿ç”¨ Promise.allSettledï¼Œå› ä¸ºæˆ‘ä»¬æƒ³çŸ¥é“æ¯ä¸€é¢—å°è¡Œæ˜Ÿçš„æ‰«æç»“æœï¼Œ
  // å³ä½¿å…¶ä¸­ä¸€äº›å¤±è´¥äº†ä¹Ÿä¸æƒ³ä¸­æ–­æ•´ä¸ªä»»åŠ¡ã€‚
  const results = await Promise.allSettled(scanPromises);

  console.log("\n--- ä»»åŠ¡æŠ¥å‘Š ---");
  let successfulScans = 0;

  results.forEach((result, index) => {
    const asteroidName = asteroids[index];
    if (result.status === 'fulfilled') {
      console.log(`âœ… [${asteroidName}] æ‰«ææˆåŠŸ: ${result.value}`);
      successfulScans++;
    } else {
      console.error(`âŒ [${asteroidName}] æ‰«æå¤±è´¥: ${result.reason.message}`);
    }
  });

  console.log("\n--- ä»»åŠ¡æ€»ç»“ ---");
  if (successfulScans > 0) {
    console.log(`ğŸ‰ ä»»åŠ¡éƒ¨åˆ†æˆåŠŸï¼å…±åœ¨ ${successfulScans} é¢—å°è¡Œæ˜Ÿä¸Šå‘ç°çŸ¿ç‰©ã€‚è¿”èˆªï¼`);
  } else {
    console.log("ğŸ˜­ æ‰€æœ‰æ‰«æå‡å¤±è´¥ã€‚çœ‹æ¥æˆ‘ä»¬ä»Šå¤©è¿æ°”ä¸ä½³ï¼Œå‡†å¤‡è¿”èˆªå§ã€‚");
  }
}

