  try {
    // ç¬¬ä¸€æ­¥ï¼šå‘é€è¯·æ±‚
    const response = await fetch(apiUrl);

    // æ£€æŸ¥AIæ˜¯å¦èƒ½æˆåŠŸè¿æ¥åˆ°ç½‘ç»œ
