function fetchAlienDictionary(word) {
  console.log(`[å‘é€è¯·æ±‚] æ­£åœ¨æŸ¥è¯¢å¤–æ˜Ÿè¯è¯­ '${word}'...`);
  const dictionary = {
    'Grok': 'æ·±åˆ»ç†è§£',
    'Zorp': 'æ”»å‡»',
    'Flibbertigibbet': 'ä¸€ä¸ªå–‹å–‹ä¸ä¼‘çš„äºº',
    'Klaatu': 'å’Œå¹³',
    'Barada': 'è­¦å‘Š',
