async function fakeSecureApiCall(url, options) {
  console.log(`[API] æ­£åœ¨å‘ ${url} å‘é€è¯·æ±‚ï¼Œé…ç½®ä¸º:`, options);
  if (options.headers && options.headers['Authorization'] === 'Bearer secret-token') {
