async function sendIntergalacticMessage(planet, message) {
  console.log(`[ç½‘ç»œå±‚] æ­£åœ¨å‘ ${planet} ä¼ è¾“æ¶ˆæ¯...`);
  console.log(`[ç½‘ç»œå±‚] ä¼ è¾“å†…å®¹:`, message);

