function getCookie(name) {
  // 1. è·å–æ‰€æœ‰cookieå­—ç¬¦ä¸²
  const allCookies = document.cookie;
  console.log("è·å–åˆ°çš„å®Œæ•´Cookieå­—ç¬¦ä¸²:", allCookies);

  // 2. æŒ‰åˆ†å·å’Œç©ºæ ¼åˆ†å‰²æˆæ•°ç»„
  const cookiePairs = allCookies.split('; ');

  // 3. éå†æ•°ç»„å¯»æ‰¾ç›®æ ‡cookie
  for (let i = 0; i < cookiePairs.length; i++) {
    const pair = cookiePairs[i].split('=');
    const key = pair[0];
    const value = pair[1];
    if (key === name) {
      // æ‰¾åˆ°äº†ï¼è¿”å›è§£ç åçš„å€¼
      return decodeURIComponent(value);
    }
  }
  // æ²¡æ‰¾åˆ°
  return null;
}

